<?php$compressor=new Compressor();// css$compressor->addCss('/admin/fw/media/css/defaults.css');$compressor->addCss('/admin/fw/media/css/admin.css');if( file_exists(SITE_DIR.'/css/admin.css') ){	$compressor->addCss('/css/admin.css');}elseif( file_exists(SITE_DIR.'/media/css/admin.css') ){	$compressor->addCss('/media/css/admin.css');}if(EDIT_MODE===true){	$compressor->addCss('/admin/fw/media/lib/jquery-ui-1.8.6.custom/css/admin-theme/jquery-ui-1.8.6.custom.css');}if( USE_SHADOWBOX===true ){	$compressor->addCss('/admin/fw/media/lib/shadowbox-3.0.3/shadowbox.css');}// js$compressor->addJs('/admin/fw/media/js/jquery.js',false);$compressor->addJs('/admin/fw/media/js/jquery.utils.js',true);$compressor->addJs('/admin/fw/media/js/jquery.form.js',true);// <script type="text/javascript" charset="utf-8" src="'.fileLastModified('/admin/fw/media/js/jquery.cookies.js').'"></script>// <script type="text/javascript" charset="utf-8" src="'.fileLastModified('/admin/fw/media/js/jquery.selectboxes.js').'"></script>if( USE_SWFOBJECT===true ){	$compressor->addJs('/admin/fw/media/js/swfobject2.js',false);}if( USE_SHADOWBOX===true ){	$compressor->addJs('/admin/fw/media/lib/shadowbox-3.0.3/shadowbox.js',false);}if(EDIT_MODE===true){	// jquery UI	$compressor->addJs('/admin/fw/media/lib/jquery-ui-1.8.6.custom/js/jquery-ui-1.8.6.custom.min.js',false);	$compressor->addJs('/admin/fw/media/lib/jquery-ui-1.8.6.custom/js/jquery.ui.datepicker-ru.js',true);}$compressor->addJs('/admin/fw/media/js/admin.js',true);$result='<!DOCTYPE html><html>	<head>		<title>FW</title>		'.$compressor->getOutCss('/admin/fw/media/css/').'		'.$compressor->getOutJs('/admin/fw/media/js/').'		<script type="text/javascript" charset="utf-8" src="/admin/fw/media/lib/ckeditor/ckeditor.js"></script>		<script type="text/javascript" charset="utf-8" src="/admin/fw/media/lib/AjexFileManager/ajex.js"></script>		'.((USE_GMAP===true)?'<script type="text/javascript" charset="utf-8" src="http://maps.google.com/maps/api/js?sensor=false&amp;key='.GMAPS_API_KEY.'"></script>':'').'		'.((USE_YAMAP===true)?'<script type="text/javascript" charset="utf-8" src="http://api-maps.yandex.ru/1.1/index.xml?loadByRequire=1&amp;key='.MAPS_YANDEX_API_KEY.'"></script>':'').'	</head>	<body class="admin '.DOMAIN.'">		'.$this->body.'	</body></html>';