<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01//EN"	"http://www.w3.org/TR/html4/strict.dtd"><html>	<head>		<meta name="Content-Type" content="text/html; charset=utf-8">		<title>webtype</title>		<style type="text/css">			#mainBox {padding: 3em;}			form textarea {width: 100%;height: 150px;}		</style>		<?php		echo '<script type="text/javascript" charset="utf-8" src="'.fileLastModified('/admin/media/js/webtype.js').'"></script>';		echo '<script type="text/javascript" charset="utf-8" src="'.fileLastModified('/admin/media/js/jquery.js').'"></script>';		?>		<script type="text/javascript" charset="utf-8"><!--			$().ready(function(){				var $form=$('#webtypeForm');				$form.find('input[name=run]').bind('click',function(){					runWebtype();				});				$form.find('input[name=run_select]').bind('click',function(){					runWebtype({select:true});				});			})						function runWebtype(hash){				var hash=(hash || {});				var $form=$('#webtypeForm');				var text_to_webtype=$form.find('textarea[name=from]')[0].value;				var br_status=$form.find('input[name=br]')[0].checked;				var link_status=$form.find('input[name=link]')[0].checked;				var nobr_status=$form.find('input[name=nobr]')[0].checked;				var result=evWebtype.run(text_to_webtype, {br_status: br_status, link_status: link_status, nobr_status: nobr_status});				$('#result').empty().html(result);				$form.find('textarea[name=to]')[0].value=result;				if(hash.select){					$form.find('textarea[name=to]')[0].focus();					$form.find('textarea[name=to]')[0].select();				}			}		//--></script>	</head>	<body>		<div id="mainBox">			<form id="webtypeForm" action="#">				<small>					<input type="checkbox" name="br" value="1" id="brChboxId"> <label for="brChboxId">&lt;p&gt;&hellip;&lt;/p&gt;</label><br>					<input type="checkbox" name="link" value="1" id="linkChboxId"> <label for="linkChboxId">&lt;a href="&hellip;"&gt;&hellip;&lt;/a&gt;</label><br>					<input type="checkbox" name="nobr" value="1" id="nobrChboxId"> <label for="nobrChboxId">&lt;span class="nobr"&gt;&hellip;&lt;/span&gt;</label><br>				</small>				<p><textarea name="from"></textarea></p>				<p><input type="button" name="run" value="Run webtype"> <input type="button" name="run_select" value="Run webtype &amp; Select result"></p>				<p><textarea name="to"></textarea></p>			</form>			<div id="result"></div>		</div>	</body></html>